userDefinedLinkHintCss:
div > .vimiumHintMarker {
/* linkhint boxes */
background: linear-gradient(105.66deg, #282727 0%, #0D0C0C 103.4%);
border: 1px solid #E6C384;
border-radius: 4.14955px;
padding: 3px 6px;
}

div > .vimiumHintMarker > .matchingCharacter {
font-style: italic;
font-weight: 200;
text-transform: uppercase;
color: #625E5A;
}

div > div.internalVimiumHintMarker > span,
div > div.vimiumHintMarker > span {
  font-family: "ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, 'Liberation Mono', 'Courier New', monospace"
  font-style: normal;
  font-weight: 800;
  font-size: 12.68px;
  letter-spacing: 0.1em;
  text-transform: uppercase;
  color: #E6C384;
}
